# Добавить текст
def insert_text(text):
    text_widget.delete("1.0", tk.END)
    text_widget.insert(tk.END, text + '\n')

# Получить текст 
def get_text():
    content = text_widget.get("1.0", tk.END)
    return content

# Функцию для отображения превью
def show_image_for_time(image_data, duration):
    # Создаем окно Tkinter
    root = tk.Tk()
    root.overrideredirect(True)  # Убрать рамку и заголовок у окна
    root.title("Предпросмотр изображения")
    root.geometry("600x200")  # Задаем размеры окна

    # Преобразование base64 в изображение
    image_bytes = base64.b64decode(image_data)
    image = Image.open(io.BytesIO(image_bytes))
    image = image.resize((600, 200), Image.ANTIALIAS)  # Изменяем размер изображения
    photo = ImageTk.PhotoImage(image)

    # Создаем виджет Label для отображения изображения
    label = tk.Label(root, image=photo)
    label.pack()

    # Получаем размеры экрана
    screen_width = root.winfo_screenwidth()
    screen_height = root.winfo_screenheight()

    # Вычисляем координаты для центрирования окна
    x = (screen_width - 600) // 2
    y = (screen_height - 200) // 2

    # Центрируем окно
    root.geometry(f"600x200+{x}+{y}")

    # Отображаем изображение на заданное время
    root.after(duration * 1000, root.destroy)

    root.mainloop()

# Удаление файла
def delete_file(file_name):
    try:
        os.remove(file_name)
    except FileNotFoundError:
        print("Файл '{}' не найден.".format(file_name))
    except Exception as e:
        print("Произошла ошибка при удалении файла '{}':".format(file_name), e)

# Чтение файла 
def read_file(file_name):
    try:
        with open(file_name, 'r', encoding='utf-8') as file:
            lines = file.readlines()
            # Используем метод join() для объединения всех строк в одну строку
            return ''.join(lines)
    except FileNotFoundError:
        print("Файл '{}' не найден.".format(file_name))
    except Exception as e:
        print("Произошла ошибка при чтении файла:", e)

# Генерация ключа для шифра 
def key_gen(txtk):
    custom_key = txtk.encode('utf-8')

    if len(custom_key) < 32:
        custom_key += b'\0' * (32 - len(custom_key))
    elif len(custom_key) > 32:
        custom_key = custom_key[:32]
    
    return base64.urlsafe_b64encode(custom_key)

# Шифрование данных
def crypt(txtk, key):
    encoded_key = key_gen(key)
    cipher = Fernet(encoded_key)
    text = read_file(txtk).encode('utf-8')
    data = cipher.encrypt(text)

    with open(txtk+'.shtr', 'w', encoding='utf-8') as file:
        hash_md5 = hashlib.md5()
        hash_md5.update(key.encode('utf-8'))
        hashed_key = hash_md5.hexdigest()

        # Запишите ключ на первой строке
        file.write(hashed_key + '\n')
        # Запишите данные на второй строке
        file.write(data.decode('utf-8'))

    delete_file(txtk)
    return data

# Десшифровка данных
def decrypt(file, key):
    filen = file
    filend = file
    hash_md5 = hashlib.md5()
    hash_md5.update(key.encode('utf-8'))
    hashed_key = hash_md5.hexdigest()

    # Откройте файл для чтения
    with open(file, 'r', encoding='utf-8') as file:
        keyf = file.readline().rstrip('\n')
        dataf = file.readline().rstrip('\n')

    if(keyf == hashed_key):
        encoded_key = key_gen(key)
        cipher = Fernet(encoded_key)
        data = cipher.decrypt(dataf.encode('utf-8')).decode('utf-8')
        with open(filen[:-5], 'w', encoding='utf-8') as filen:
            filen.write(data)

        delete_file(filend)
    else:
        data = 'Invalid key'

    return data

# Выборка файла
def select_file():
    file_path = filedialog.askopenfilename()
    entry_file.delete(0, tk.END)
    entry_file.insert(0, file_path)
    text = read_file(file_path)
    insert_text(text)

# Обработка файла
def process_file():
    label_result.config(text='Операция в процессе...')
    file_path = entry_file.get()
    key = entry_key.get()
    method = var.get()

    if(method == 'Шифровать'):
        crypt(file_path, key)
        text = read_file(file_path+'.shtr')
        insert_text(text)
        label_result.config(text='Файл успешно зашифрован!')
    else:
        decrypted_text = decrypt(file_path,key)
        if(decrypted_text == 'Invalid key'):
            label_result.config(text='Неверный ключь для расшифровки!')
        else:
            text = read_file(file_path[:-5])
            insert_text(text)
            label_result.config(text='Файл успешно расшифрован!')

